import { animated, useSpring } from 'react-spring';
import '../styles/elevatorStyles.css';
import { useState } from 'react';

const Elevator = ({ y, style, handleElevatorArrived, color }) => {
  const [isMoving, setIsMoving] = useState(false);

  const spring = useSpring({
    to: { y },
    config: {
      clamp: true,
      tension: 20,
      friction: 30,
    },
    onRest: () => {
      if (handleElevatorArrived) {

        handleElevatorArrived();
      }else{
        console.log("wtf was send here",handleElevatorArrived)
      }
      setIsMoving(false);
    },
    onStart: () => {
      setIsMoving(true);
    },
    onFrame: ({ y }) => {
      const distance = Math.abs(y - spring.y.get());
      let tension = 20;
      let friction = 30;
      if (isMoving) {
        if (distance < 100) {
          tension = 120 + distance * 0.1;
          friction = 14;
        } else if (distance >= 100 && distance < 300) {
          tension = 200 + distance * 0.05;
          friction = 20;
        } else {
          tension = 80;
          friction = 26;
        }
      }
      spring.set({
        tension,
        friction,
      });
    },
  });

  return (
    <animated.div
      className="elevator"
      style={{
        ...style,
        transform: spring.y.to((y) => `translateY(${y}px)`),
      }}
    >
      <img
        className="elevator-image"
        src={`./icons8-elevator-${color}.svg`}
        alt="Woops"
      />
    </animated.div>
  );
};

export default Elevator;
