import { animated, useSpring } from 'react-spring';
import '../styles/elevatorStyles.css';

const Elevator = ({ y, style, handleElevetorArrived, color, dist }) => {
  console.log(dist);
  const spring = useSpring({
    to: { y },

    config: {
      clamp: true,
      tension: 170, // reduce the tension value to make the animation slower and smoother
      friction: 80, // increase the friction value to make the animation smoother
      duration: 6000 * dist,
      mass: 200,
    },

    onRest: () => {
      handleElevetorArrived();
    },
  });

  const finalDestinationY = y;

  const [reachedDestination, setReachedDestination] = React.useState(false);

  React.useEffect(() => {
    if (spring.y.get() >= finalDestinationY) {
      setReachedDestination(true);
    }
  }, [spring.y, finalDestinationY]);

  const oscillationAmplitude = 15;

  const oscillation = useSpring({
    to: {
      transform: reachedDestination
        ? `translateY(-${oscillationAmplitude}px)`
        : 'translateY(0px)',
    },
    from: {
      transform: reachedDestination
        ? 'translateY(0px)'
        : `translateY(-${oscillationAmplitude}px)`,
    },
    config: {
      duration: 1000,
      tension: 100,
      friction: 30,
    },
  });

  return (
    <animated.div
      className="elevator"
      style={{
        ...style,
        transform: spring.y
          .to((val) => {
            if (val < 100) {
              // Move slowly at first
              return val / 2;
            } else if (val < 300) {
              // Speed up in the middle
              return (val - 100) / 1.5 + 50;
            } else if (val < 400) {
              // Slow down before reaching a point
              return (val - 300) / 2 + 200;
            } else {
              // Reach final destination
              return finalDestinationY;
            }
          })
          .to((val) => `translateY(${val}px)`),
        ...oscillation,
      }}
    >
      <img
        className="elevator-image"
        src={`./icons8-elevator-${color}.svg`}
        alt="Woops"
      ></img>
    </animated.div>
  );
};

export default Elevator;
