import React, { useState } from 'react';
import { useSpring, animated } from 'react-spring';

const AnimatedDiv = () => {
  const [isAnimating, setIsAnimating] = useState(false);

  const handleClick = () => {
    setIsAnimating(true);
  };

  const { y, backgroundColor } = useSpring({
    from: { y: 0, backgroundColor: 'red' },
    to: [
      { y: 100, backgroundColor: 'orange' },
      { y: 0, backgroundColor: 'green' },
    ],
    config: { duration: 2000 },
    reset: isAnimating,
    reverse: isAnimating,
    onRest: () => {
      setIsAnimating(false);
    },
  });

  return (
    <animated.div
      onClick={handleClick}
      style={{
        transform: y.interpolate((y) => `translateY(${y}px)`),
        backgroundColor,
      }}
    >
      Animate me!
    </animated.div>
  );
};
