import React, { useState } from 'react';
import { useSpring, animated } from 'react-spring';

export default function MovingImage() {
  const [position, setPosition] = useState({ x: 0, y: 0 });

  const springProps = useSpring({
    to: { x: position.x, y: position.y },
    config: {
      tension: 300,
      friction: 20
    }
  });

  const handleXChange = (event) => {
    setPosition({ ...position, x: parseInt(event.target.value) });
  };

  const handleYChange = (event) => {
    setPosition({ ...position, y: parseInt(event.target.value) });
  };

  return (
    <div>
      <div>
        <label htmlFor="x-input">X:</label>
        <input type="text" id="x-input" value={position.x} onChange={handleXChange} />
      </div>
      <div>
        <label htmlFor="y-input">Y:</label>
        <input type="text" id="y-input" value={position.y} onChange={handleYChange} />
      </div>
      <animated.div
        style={{
          position: 'absolute',
          top: springProps.y,
          left: springProps.x,
          width: '50px',
          height: '50px',
          backgroundColor: 'red'
        }}
      />
    </div>
  );
}
