import React, { useState } from 'react';
import { animated, useSpring } from 'react-spring';

const ImageAnimate = () => {
  const [x, setX] = useState(0);
  const [y, setY] = useState(0);

  const [{ xy }, set] = useSpring(() => ({
    xy: [0, 0],
    config: { mass: 1, tension: 170, friction: 100 },
  }));

  const handleChange = (e, axis) => {
    const value = e.target.value;
    if (axis === 'x') {
      setX(value);
    } else {
      setY(value);
    }
  };

  const handleMove = () => {
    set({ xy: [x, y] });
  };

  return (
    <div>
      <div>
        <label>X:</label>
        <input
          type="number"
          value={x}
          onChange={(e) => handleChange(e, 'x')}
        />
      </div>
      <div>
        <label>Y:</label>
        <input
          type="number"
          value={y}
          onChange={(e) => handleChange(e, 'y')}
        />
      </div>
      <button onClick={handleMove}>Move Object</button>
      <div style={{ position: 'relative', width: '100%', height: '300px' }}>
        <animated.div
          style={{
            position: 'absolute',
            background: 'blue',
            width: '50px',
            height: '50px',
            transform: xy.interpolate((x, y) => `translate(${x}px, ${y}px)`),
          }}
        />
      </div>
    </div>
  );
};

export default ImageAnimate;
