import { animated, useSpring } from 'react-spring';
import '../styles/elevatorStyles.css';
import React from 'react';

const Elevator = ({ y, style, handleElevetorArrived, color }) => {
  const spring = useSpring({
    to: { y },

    config: {
      clamp: true,
      tension: 120, // adjust the tension value to control the speed of the elevator
      friction: 25, // adjust the friction value to control the smoothness of the animation
      precision: 0.01, // increase the precision value to make the animation smoother
    },

    onRest: () => {
      handleElevetorArrived();
    },
  });

  const [isMoving, setIsMoving] = React.useState(false);

  React.useEffect(() => {
    if (spring.y.get() !== y) {
      setIsMoving(true);
    }
  }, [spring.y, y]);

  React.useEffect(() => {
    if (!isMoving) {
      console.log('Elevator is not moving');
    } else if (spring.y.get() < 100) {
      console.log('Elevator is moving slowly');
    } else if (spring.y.get() < 300) {
      console.log('Elevator is speeding up');
    } else if (spring.y.get() < 400) {
      console.log('Elevator is slowing down');
    } else {
      console.log('Elevator has arrived');
    }
  }, [spring.y, isMoving]);

  return (
    <animated.div
      className="elevator"
      style={{
        ...style,
        transform: spring.y
          .to((val) => {
            if (val < 100) {
              // Move slowly at first
              return val / 2;
            } else if (val < 300) {
              // Speed up in the middle
              return (val - 100) / 1.5 + 50;
            } else if (val < 400) {
              // Slow down before reaching a point
              return (val - 300) / 2 + 200;
            } else {
              // Reach final destination
              return y;
            }
          })
          .to((val) => `translateY(${val}px)`),
      }}
    >
      <img
        className="elevator-image"
        src={`./icons8-elevator-${color}.svg`}
        alt="Woops"
      ></img>
    </animated.div>
  );
};

export default Elevator;
