import React, { useEffect, useRef, useState } from 'react';
import '../styles/boardStyles.css';
import Elevator from './Elevator';
import { useElevatorController } from '../context/ElevatorController';

const Board = ({ rows, columns }) => {
  const squareRef = useRef(null);
  const { handleElevatorReservation, elevatorsData, setElevatorsData, buttonColors, setButtonColors } = useElevatorController(columns, rows);

  const [width, setWidth] = useState(0);
  const [height, setHeight] = useState(0);

  //get width and height for the style of the elevator
  useEffect(() => {
    if (squareRef.current) {
      const { width, height } = squareRef.current.getBoundingClientRect();
      setWidth(width);
      setHeight(height);
    }
  }, []);

  //create the board row and columns
  const createBoard = () => {
    const board = [];
    for (let i = 0; i < rows; i++) {
      const row = [];
      for (let j = 0; j < columns; j++) {
        row.push(
          <div key={`cell-${i}-${j}`} className="square p-2 bg-white" ref={i === 0 && j === 0 ? squareRef : null} />
        );
      }
      board.push(
        <div key={`row-${i}`} className="d-flex flex-row align-items-center">
          <div className="row-number me-2">{`${rows - i - 1 === 0 ? 'Ground Floor' : rows - i - 1 === 1 ? '1st' : rows - i - 1 === 2 ? '2nd' : rows - i - 1 === 3 ? '3rd' : `${rows - i - 1}th`}`}</div>
          {row}
          <button
            type="button"
            id={`button${rows - i - 1}`}
            disabled={false}
            className="elevator-button-arrived"
            style={{ backgroundColor: buttonColors[rows - i - 1] }}
            onClick={() => handleElevatorReservation(rows - i - 1)}
          >
            Call
          </button>
        </div>
      );
    }
    return board;
  };

  return (
    <div className="board">
      {createBoard()}
      {elevatorsData.map((data) => (
        <Elevator
          key={data.key}
          y={data.y}
          style={{ backgroundColor: 'green', width: `${width}px`, left: `${data.key * (width - 2)}px` }}
          handleElevatorArrived={data.handleElevatorArrived}
          currFloor={data.currFloor}
          toFloor={data.toFloor}
          occupied={data.occupied}
          setElevatorsData={setElevatorsData}
          height={height}
        />
      ))}
    </div>
  );
};

export default Board;
