import { useSpring, animated } from 'react-spring';
import { useState } from 'react';

export function Elevator2({ initialY }) {
  const [y, setY] = useState(initialY);
  const [{ y: animatedY }, setAnimatedY] = useSpring(() => ({ y: initialY }));

  const handleUpClick = () => {
    setY(prevY => prevY - 50);
  };

  const handleDownClick = () => {
    setY(prevY => prevY + 50);
  };

  useEffect(() => {
    setAnimatedY({ y: y, config: { mass: 1, tension: 180, friction: 12 } });
  }, [y]);

  return (
    <div>
      <h1>{y}</h1>
      <div>
        <button onClick={handleUpClick}>Up</button>
        <button onClick={handleDownClick}>Down</button>
      </div>
      <div onClick={() => setY(y + 50)}>
        <animated.div
          style={{
            width: '50px',
            height: '50px',
            backgroundColor: 'red',
            position: 'absolute',
            bottom: 0,
            transform: animatedY.interpolate(y => `translate3d(0, ${y}px, 0)`),
          }}
        />
      </div>
    </div>
  );
}
