const Elevator = ({ y, style, handleElevetorArrived, color }) => {
  const distance = Math.abs(y - style.y); // calculate the distance traveled by the elevator
  const duration = distance / ELEVATOR_SPEED * 1000; // calculate the animation duration based on the distance and speed

  const spring = useSpring({
    from: { y: style.y },
    to: { y },
    config: {
      clamp: true,
      duration,
      mass: 20,
      tension: 80,
      friction: 14,
    },
    onRest: () => {
      handleElevetorArrived();
    },
  });

  const translateY = spring.y.interpolate({
    range: [style.y, y],
    output: [style.y, y].map(y => `translateY(${y}px)`),
  });

  return (
    <animated.div
      className="elevator"
      style={{
        ...style,
        transform: translateY,
      }}
    >
      <img className="elevator-image" src={`./icons8-elevator-${color}.svg`} alt = "Woops"></img>
    </animated.div>
  );
};
