import { animated, useSpring } from 'react-spring';
import { useEffect, useState } from 'react';
import '../styles/elevatorStyles.css'

const Elevator = ({ y, style, handleElevetorArrived, color }) => {
  const [targetY, setTargetY] = useState(y);
  const [isAnimating, setIsAnimating] = useState(false);

  useEffect(() => {
    if (isAnimating) {
      handleElevetorArrived();
      setIsAnimating(false);
    }
  }, [isAnimating, handleElevetorArrived]);

  useEffect(() => {
    setTargetY(y);
    setIsAnimating(true);
  }, [y]);

  const distance = Math.abs(targetY - style.y);

  const spring = useSpring({
    to: { y: targetY },
    config: {
      clamp: true,
      mass: 20,
      tension: 120,
      friction: 26,
      duration: distance * 10,
    },
  });

  return (
    <animated.div
      className="elevator"
      style={{
        ...style,
        transform: spring.y.to(y => `translateY(${y}px)`),
      }}
    >
      <animated.img
        className="elevator-image"
        src={`./icons8-elevator-${color}.svg`}
        alt="Woops"
      />
    </animated.div>
  );
};

export default Elevator;
