import React, { useState } from 'react';
import { animated, useSpring, config } from 'react-spring';
import '../styles/movingObjectStyles.css'

const MovingObject = () => {
  const [x, setX] = useState(0);
  const [y, setY] = useState(0);
  const [speed, setSpeed] = useState(40);
  const [slowdown, setSlowdown] = useState(1);

  const tension = 170 * slowdown;
  const friction = speed / slowdown;

  const [{ xy }, set] = useSpring(() => ({
    xy: [0, 0],
    config: { mass: 1, tension: tension, friction: friction },
  }));

  const handleChange = (e, field) => {
    const value = e.target.value;
    if (field === 'x') {
      setX(value);
    } else if (field === 'y') {
      setY(value);
    } else if (field === 'speed') {
      setSpeed(value);
    } else {
      setSlowdown(value);
    }
  };

  const handleMove = () => {
    set({ xy: [x, y], config: { mass: 1, tension: tension, friction: friction } });
  };

  return (
    <div className="container">
      <div className="control">
        <label>X:</label>
        <input
          type="number"
          value={x}
          onChange={(e) => handleChange(e, 'x')}
        />
      </div>
      <div className="control">
        <label>Y:</label>
        <input
          type="number"
          value={y}
          onChange={(e) => handleChange(e, 'y')}
        />
      </div>
      <div className="control">
        <label>Speed:</label>
        <input
          type="number"
          value={speed}
          onChange={(e) => handleChange(e, 'speed')}
        />
      </div>
      <div className="control">
        <label>Slowdown:</label>
        <input
          type="number"
          value={slowdown}
          onChange={(e) => handleChange(e, 'slowdown')}
        />
          <button onClick={handleMove}>Move Object</button>

      </div>
      <div className="position-area">
        <animated.div
          className="moving-object"
          style={{
            transform: xy.to((x, y) => `translate(${x}px, ${y}px)`),
          }}
        />
      </div>
    </div>
  );
};

export default MovingObject;
