import { useSpring, animated } from 'react-spring';
import { useEffect } from 'react';

export function Elevator2({ y }) {
  const [{ y: animatedY }, setAnimatedY] = useSpring(() => ({ y }));

  useEffect(() => {
    setAnimatedY({ y, config: { mass: 1, tension: 180, friction: 12 } });
  }, [y]);

  return (
    <div>
      <animated.div
        style={{
          width: '50px',
          height: '50px',
          backgroundColor: 'red',
          position: 'absolute',
          bottom: 0,
          transform: animatedY.to(y => `translate3d(0, ${y}px, 0)`),
        }}
      />
    </div>
  );
}
