import React, { useState } from 'react';
import '../styles/boardStyles.css';
import Box from './Box';
import { useSpring, animated } from 'react-spring';

function Board() {
  const [rows, setRows] = useState(11); // Default value of 4 rows
  const [columns, setColumns] = useState(5); // Default value of 4 columns

  function handleRowsChange(event) {
    setRows(event.target.value);
  }

  function handleColumnsChange(event) {
    setColumns(event.target.value);
  }

  return (
    <>
      <div>
        <label htmlFor="rows">Rows:</label>
        <input id="rows" type="number" min="1" value={rows} onChange={handleRowsChange} />
      </div>
      <div>
        <label htmlFor="columns">Columns:</label>
        <input id="columns" type="number" min="1" value={columns} onChange={handleColumnsChange} />
      </div>


      <div className="board" style={{'--columns': columns, '--rows': rows}}>
        {[...Array(rows * columns)].map((_, i) => (
            <Box key={i}/>
        ))}
      </div>
    </>
  );
}

export default Board;
