import { useSpring, animated } from 'react-spring';
import { useEffect } from 'react';

// Custom interpolation function that uses easing to start and stop the animation smoothly
const interpolateEasing = (start, stop) => (t) =>
  start + (stop - start) * (t < 0.5 ? 2 * t * t : -1 + (4 - 2 * t) * t);

export function Elevator2({ y }) {
  const [{ y: animatedY }, setAnimatedY] = useSpring(() => ({ y }));

  useEffect(() => {
    // Get the current y value from the animated spring
    const currentY = animatedY.getValue().y;

    // Calculate the distance between the current y value and the new y value
    const distance = y - currentY;

    // Calculate the velocity needed to reach the new y value in 1 second using easing
    const velocity = distance / interpolateEasing(1, 0)(0.5);

    // Update the animated spring with the new y value and velocity
    setAnimatedY({
      y,
      velocity: { y: velocity },
      config: { mass: 1, tension: 180, friction: 12 },
    });
  }, [y]);

  return (
    <div style={{ width: '50px', height: '200px' }}>
      <animated.div
        style={{
          width: '50px',
          height: '50px',
          backgroundColor: 'red',
          position: 'absolute',
          bottom: 0,
          transform: animatedY.interpolate(interpolateEasing(0, 200)),
          zIndex: 100,
        }}
      />
    </div>
  );
}
