import React, { useRef } from 'react';
import PropTypes from 'prop-types';
import '../styles/floorStyles.css';

const Floor = ({ columns, rowNum, handleElevatorReservation, buttonColors }) => {
  const squareRef = useRef(null);

  const createFloor = () => {
    const floor = [];
    for (let i = 0; i < columns; i++) {
      floor.push(
        <div
          key={`cell-${rowNum}-${i}`}
          className="square p-2 bg-white"
          ref={rowNum === 0 && i === 0 ? squareRef : null}
        />
      );
    }
    return floor;
  };

  return (
    <div key={`row-${rowNum}`} className="d-flex flex-row align-items-center">
      <div className="row-number me-2">{`${rowNum === 0 ? 'Ground Floor' : rowNum === 1 ? '1st' : rowNum === 2 ? '2nd' : rowNum === 3 ? '3rd' : `${rowNum}th`}`}</div>
      {createFloor()}
      <button
        type="button"
        id={`button-${rowNum}`}
        disabled={false}
        className="elevator-button-arrived"
        style={{ backgroundColor: buttonColors[rowNum] }}
        onClick={() => handleElevatorReservation(rowNum)}
      >
        Call
      </button>
    </div>
  );
};

Floor.propTypes = {
  columns: PropTypes.number.isRequired,
  rowNum: PropTypes.number.isRequired,
  handleElevatorReservation: PropTypes.func.isRequired,
  buttonColors: PropTypes.arrayOf(PropTypes.string).isRequired,
};

export default Floor;
