import React, { useEffect, useRef, useState } from 'react';
import '../styles/boardStyles.css';

const Board = ({ rows, columns }) => {
  const squareRef = useRef(null);
  const [buttonColors, setButtonColors] = useState(Array(rows).fill('green'));

  useEffect(() => {
    if (squareRef.current) {
      const { width, height, top, left } = squareRef.current.getBoundingClientRect();
      console.log("Width X:", width, "Height Y:", height);
    }
  }, []);

  function handleElevatorReservation(rowNum, event) {
    console.log(event.target);

    setButtonColors((prevButtonColors) => {
      const newButtonColors = [...prevButtonColors];
      newButtonColors[rowNum] = 'red';
      return newButtonColors;
    });

    setTimeout(() => {
      setButtonColors((prevButtonColors) => {
        const newButtonColors = [...prevButtonColors];
        newButtonColors[rowNum] = 'green';
        return newButtonColors;
      });
    }, 5000);
  }


  const createBoard = () => {
    const board = [];
    for (let i = 0; i < rows; i++) {
      const row = [];
      for (let j = 0; j < columns; j++) {
        row.push(
          <div key={`cell-${i}-${j}`} className="square p-2 bg-white" ref={i === 0 && j === 0 ? squareRef : null} />
        );
      }
      board.push(
        <div key={`row-${i}`} className="d-flex flex-row align-items-center">
          <div className="row-number me-2">{`${rows-i-1==0?"Ground Floor" : rows-i-1===1?"1st":rows-i-1===2?"2nd": rows-i-1===3?"3rd":`${rows-i-1}th`}`}</div>
          {row}
          <button className="elevator-button" style={{ backgroundColor: buttonColors[rows-i-1]}} onClick={(event)=>handleElevatorReservation(rows-i-1,event)} >Call</button>
        </div>
      );
    }
    return board;
  };

  return <div className="board">{createBoard()}</div>;
};

export default Board;
