import React, { useRef, useState, useImperativeHandle, forwardRef, useEffect } from 'react';
import { animated, useSpring, config } from 'react-spring';
import '../styles/movingObjectStyles.css'

const MovingObject2 = ({ y , style}) => {
  const spring = useSpring({
    to: { y },
    config: {
      duration: 2000, // set a longer duration for the animation
      tension: 70, // reduce the tension value to make the animation slower
      friction: 15, // increase the friction value to make the animation smoother
      onRest: () => {
        if (typeof onArrive === 'function') {
          onArrive();
        }
      },
    },
  });

  return (
    <animated.div
      className="moving-object"
      style={{
        ...style,
        transform: spring.y.interpolate(y => `translateY(${y}px)`),

      }}
    />
  );
};

export default MovingObject2;
