import React, { useState } from 'react';
import { animated, useSpring } from 'react-spring';

const MovingImage = () => {
  const [x, setX] = useState(0);
  const [y, setY] = useState(0);
  const [speed, setSpeed] = useState(40);

  const [{ xy }, set] = useSpring(() => ({
    xy: [0, 0],
    config: { mass: 1, tension: 170, friction: speed },
  }));

  const handleChange = (e, field) => {
    const value = e.target.value;
    if (field === 'x') {
      setX(value);
    } else if (field === 'y') {
      setY(value);
    } else {
      setSpeed(value);
    }
    set({ xy: [x, y] });
  };

  const handleMove = () => {
    set({ xy: [x, y], config: { mass: 1, tension: 170, friction: speed } });
  };

  return (
    <div>
      <div>
        <label>X:</label>
        <input
          type="number"
          value={x}
          onChange={(e) => handleChange(e, 'x')}
        />
      </div>
      <div>
        <label>Y:</label>
        <input
          type="number"
          value={y}
          onChange={(e) => handleChange(e, 'y')}
        />
      </div>
      <div>
        <label>Speed:</label>
        <input
          type="number"
          value={speed}
          onChange={(e) => handleChange(e, 'speed')}
        />
      </div>
      <button onClick={handleMove}>Move Object</button>
      <div style={{ position: 'relative', width: '100%', height: '300px' }}>
        <animated.div
          style={{
            position: 'absolute',
            background: 'blue',
            width: '50px',
            height: '50px',
            transform: xy.interpolate((x, y) => `translate(${x}px, ${y}px)`),
          }}
        />
      </div>
    </div>
  );
};

export default MovingImage;
