import React, { useState, useEffect } from 'react';
import Countdown from 'react-countdown';

const CountDownTimer = ({ onStart, onStop, onReset }) => {
  const [isRunning, setIsRunning] = useState(false);
  const [time, setTime] = useState(300000); // initial time in milliseconds

  useEffect(() => {
    if (onStart) {
      setIsRunning(true);
    } else if (onStop) {
      setIsRunning(false);
    } else if (onReset) {
      setIsRunning(false);
      setTime(300000);
    }
  }, [onStart, onStop, onReset]);

  const renderer = ({ minutes, seconds }) => {
    return (
      <div>
        <span>{minutes}</span>:<span>{seconds}</span>
      </div>
    );
  };

  return (
    <div>
      <Countdown
        date={Date.now() + time}
        autoStart={false}
        onComplete={() => setIsRunning(false)}
        renderer={renderer}
        running={isRunning}
      />
      <button onClick={() => {
        setIsRunning(true);
        onStart && onStart();
      }}>Start</button>
      <button onClick={() => {
        setIsRunning(false);
        onStop && onStop();
      }}>Stop</button>
      <button onClick={() => {
        setIsRunning(false);
        setTime(300000);
        onReset && onReset();
      }}>Reset</button>
    </div>
  );
};

export default CountDownTimer;
