import { animated, useSpring } from 'react-spring';
import '../styles/movingObjectStyles.css'

const Elevator = ({ y , style, handleElevetorArrived, color}) => {
  const yOffset = 10 // the amount of vertical offset

  const spring = useSpring({
    from: { y: 0 },
    to: async (next) => {
      while (true) {
        await next({ y: yOffset, reset: true }) // move up
        await next({ y, reset: true }) // move down
        await next({ y: 0, reset: true }) // back to original position
      }
    },
    config: {
      tension: 80, // adjust the tension to make the animation slower or faster
      friction: 15, // adjust the friction to make the animation smoother or rougher
      clamp: true, // enable clamp to prevent overshooting
    },
    onRest: () => {
      handleElevetorArrived();
    },
  });

  return (
    <animated.div
      className="moving-object"
      style={{
        ...style,
        transform: spring.y.to(y => `translateY(${y}px)`),        
      }}>
      <img className="elevator-image" src={`./icons8-elevator-${color}.svg`} alt="elevator"></img>
    </animated.div>
  );
};

export default Elevator;
