import React, { useState, useEffect } from 'react';
import Countdown from 'react-countdown';
import '../styles/timerStyles.css'

const CountDownTimer = ({ stop, start, reset }) => {
  const [isRunning, setIsRunning] = useState(false);
  const [time, setTime] = useState(300000); // initial time in milliseconds

  useEffect(() => {
    if (start) {
      setIsRunning(true);
    } else if (stop) {
      setIsRunning(false);
    } else if (reset) {
      setIsRunning(false);
      setTime(300000);
    }
  }, [start, stop, reset]);

  const renderer = ({ minutes, seconds }) => {
    return (
      <div>
        <span>{minutes}</span>:<span>{seconds}</span>
      </div>
    );
  };

  return (
    <div>
      <Countdown
        date={Date.now() + time}
        autoStart={false}
        onComplete={() => setIsRunning(false)}
        renderer={renderer}
        running={isRunning}
      />
    </div>
  );
};

export default CountDownTimer;
