import { useState, useEffect, useRef } from 'react';
import { useSpring, animated } from 'react-spring';

export function Elevator2({ y }) {
  const [targetY, setTargetY] = useState(y);
  const prevYRef = useRef(null);

  useEffect(() => {
    prevYRef.current = targetY;
  }, [targetY]);

  const spring = useSpring({
    to: async (next) => {
      await next({ y: targetY - 50, config: { tension: 100, friction: 10 } });
      await next({ y: targetY + 20, config: { tension: 200, friction: 20 } });
      await next({ y: targetY, config: { tension: 150, friction: 30 } });
    },
    from: { y: 0 },
    config: { duration: 1000 },
  });

  const animationRef = useRef(null);

  useEffect(() => {
    const update = () => {
      const currentY = prevYRef.current;
      if (currentY !== targetY) {
        setTargetY(targetY);
      }
      animationRef.current = requestAnimationFrame(update);
    };
    animationRef.current = requestAnimationFrame(update);
    return () => cancelAnimationFrame(animationRef.current);
  }, [targetY]);

  return <animated.div style={{ transform: `translateY(${spring.y}px)` }}>Animated Object</animated.div>;
}
