import React, { useEffect, useRef } from 'react';
import '../styles/boardStyles.css';

const Board = ({ rows, columns }) => {
  const squareRef = useRef(null);

  useEffect(() => {
    if (squareRef.current) {
      const { width, height, top, left } = squareRef.current.getBoundingClientRect();
      const centerX = left + width / 2;
      const centerY = top + height / 2;
      console.log("Center X:", centerX, "Center Y:", centerY);
    }
  }, []);

  const createBoard = () => {
    const board = [];
    for (let i = 0; i < rows; i++) {
      const row = [];
      for (let j = 0; j < columns; j++) {
        row.push(
          <div key={`cell-${i}-${j}`} className="square p-2 bg-white" ref={i === 0 && j === 0 ? squareRef : null} />
        );
      }
      board.push(
        <div key={`row-${rows-i}`} className="d-flex flex-row align-items-center">
          <div className="row-number me-2">{i + 1}</div>
          {row}
        </div>
      );
    }
    return board;
  };

  return <div className="board">{createBoard()}</div>;
};

export default Board;
