import React, { useState } from 'react';
import { useSpring, animated } from 'react-spring';

export default function  MovingImage() {
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [target, setTarget] = useState({ x: 0, y: 0 });
  const [speed, setSpeed] = useState(2);

const springProps = useSpring({
  to: target,
  config: (current) => ({
    velocity: distance(position, target) / 1000 * (1 / speed),
    tension: 80,
    friction: 30,
    precision: 0.1
  })
});

  const distance = (pos1, pos2) => {
    const dx = pos1.x - pos2.x;
    const dy = pos1.y - pos2.y;
    return Math.sqrt(dx * dx + dy * dy);
  };

  const handleXChange = (event) => {
    setPosition({ ...position, x: parseInt(event.target.value) });
  };

  const handleYChange = (event) => {
    setPosition({ ...position, y: parseInt(event.target.value) });
  };

  const handleSpeedChange = (event) => {
    setSpeed(parseInt(event.target.value));
  };

  const handleButtonClick = () => {
    setTarget({ x: position.x, y: position.y });
  };

  return (
    <div>
      <div>
        <label htmlFor="x-input">X:</label>
        <input type="text" id="x-input" value={position.x} onChange={handleXChange} />
      </div>
      <div>
        <label htmlFor="y-input">Y:</label>
        <input type="text" id="y-input" value={position.y} onChange={handleYChange} />
      </div>
      <div>
        <label htmlFor="speed-input">Speed:</label>
        <input type="text" id="speed-input" value={speed} onChange={handleSpeedChange} />
      </div>
      <button onClick={handleButtonClick}>Move to ({position.x}, {position.y})</button>
      <animated.div
        style={{
          position: 'absolute',
          top: springProps.y,
          left: springProps.x,
          width: '50px',
          height: '50px',
          backgroundColor: 'red'
        }}
      />
    </div>
  );
}
