import { animated, useSpring } from 'react-spring';
import '../styles/elevatorStyles.css'

const Elevator = ({ y, style, handleElevetorArrived, color }) => {
  const targetY = y;
  const distance = Math.abs(targetY - style.y);

  const spring = useSpring({
    to: { y: targetY },
    config: {
      clamp: true,
      mass: 20,
      tension: 2,
      friction: 2,
      duration: distance * 10, // Set the duration based on the distance
    },
    onRest: () => {
      handleElevetorArrived();
    },
  });

  return (
    <animated.div
      className="elevator"
      style={{
        ...style,
        transform: spring.y.to(y => `translateY(${y}px)`),
      }}
    >
      <animated.img
        className="elevator-image"
        src={`./icons8-elevator-${color}.svg`}
        alt="Woops"
      />
    </animated.div>
  );
};

export default Elevator;
