import React, { useRef, useState, useImperativeHandle, forwardRef, useEffect } from 'react';
import { animated, useSpring, config } from 'react-spring';
import '../styles/movingObjectStyles.css'

const MovingObject2 = forwardRef((props, ref) => {
  const { x: initialX, y: initialY, style, onBoardArriveEvent} = props

  useImperativeHandle(ref, () => ({
    log() {
      handleMove();
    }
  }));

  const [speed, setSpeed] = useState(40);
  const [slowdown, setSlowdown] = useState(1);

  const tension = 170 * slowdown;
  const friction = speed / slowdown;

  const [{ xy }, set] = useSpring(() => ({
    xy: [0, initialY],
    config: { mass: 1, tension: tension, friction: friction },
    onRest: onBoardArriveEvent,
  }));

  useEffect(() => {
    set({ xy: [0, props.y], config: { mass: 1, tension: tension, friction: friction } });
  }, [props.y, tension, friction]);

  const handleMove = () => {
    set({ xy: [400, props.y], config: { mass: 1, tension: 170, friction: 500 } });
  };

  return (
    <animated.div
      className="moving-object"
      style={{
        ...style,
        transform: xy.to((x, y) => `translate(${x}px, ${y}px)`),
      }}
    />
  );
});

export default MovingObject2s;
