import { animated, useSpring } from 'react-spring';
import '../styles/movingObjectStyles.css'


const Elevator = ({ y , style, handleElevetorArrived, color}) => {


  const spring = useSpring({
    to: { y },
    config: {
      // duration: 7000, // set a longer duration for the animation
      tension: 60, // reduce the tension value to make the animation slower
      friction: 18, // increase the friction value to make the animation smoother
    },
   onRest: () => {

    handleElevetorArrived();
  },
  });

  return (


    <animated.div
    className="moving-object"
    style={{
      ...style,
      transform: spring.y.to(y => `translateY(${y}px)`),        
    }}>
      {/* <div className='image-container'></div> */}
      <img className="elevator-image" src={`./icons8-elevator-${color}.svg`}></img>
    </animated.div>
  );
};

export default Elevator;
