import { useSpring, animated } from 'react-spring';

export function Elevator2() {
  const [{ y }, set] = useSpring(() => ({ y: 0 }));

  const handleClick = e => {
    set({
      y: e.clientY,
      config: { mass: 1, tension: 100, friction: 40 },
      onChange: ({ value }) => {
        if (value.y >= 200) {
          console.log('Object reached the point!');
        }
      }
    });
  };

  return (
    <div onClick={handleClick}>
      <animated.div
        style={{
          transform: y.interpolate(y => `translate3d(0, ${y}px, 0)`),
        }}
      >
        My animated object
      </animated.div>
    </div>
  );
}
