import React, { useState } from "react";
import { useSpring, animated } from "react-spring";

function MovingImage() {
  const [isMoving, setIsMoving] = useState(false);
  const [targetPosition, setTargetPosition] = useState({ x: 0, y: 0 });
  const [xInputValue, setXInputValue] = useState("");
  const [yInputValue, setYInputValue] = useState("");

  const handleChangeX = (event) => {
    setXInputValue(event.target.value);
  };

  const handleChangeY = (event) => {
    setYInputValue(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setTargetPosition({ x: xInputValue, y: yInputValue });
    setIsMoving(true);
  };

  const props = useSpring({
    to: { left: `${targetPosition.x}px`, top: `${targetPosition.y}px` },
    from: { left: "0px", top: "0px" },
    reset: true,
    reverse: !isMoving,
    config: { tension: 200, friction: 20 },
    onRest: () => setIsMoving(false),
  });

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          X:
          <input type="text" value={xInputValue} onChange={handleChangeX} />
        </label>
        <br />
        <label>
          Y:
          <input type="text" value={yInputValue} onChange={handleChangeY} />
        </label>
        <br />
        <button type="submit">Move Image</button>
      </form>
      <div style={{ position: "relative" }}>
        <animated.img
          src="https://via.placeholder.com/150"
          style={props}
          alt="Moving Image"
        />
      </div>
    </div>
  );
}

export default MovingImage;
