import React, { useState } from "react";
import { useSpring, animated } from "react-spring";

function ImageAnomate() {
  const [isMoving, setIsMoving] = useState(false);

  const props = useSpring({
    to: async (next, cancel) => {
      while (isMoving) {
        await next({ left: "50%" });
        await next({ left: "0%" });
      }
    },
    from: { left: "0%" },
    config: { tension: 200, friction: 20 },
  });

  const startMoving = () => {
    setIsMoving(true);
  };

  const stopMoving = () => {
    setIsMoving(false);
  };

  return (
    <div onMouseEnter={startMoving} onMouseLeave={stopMoving}>
      <animated.img
        src="https://via.placeholder.com/150"
        style={props}
        alt="Moving Image"
      />
    </div>
  );
}

export default MovingImage;
