import { animated, useSpring } from 'react-spring';
import '../styles/elevatorStyles.css';

const ELEVATOR_SPEED = 100;

const Elevator = ({ y, style, handleElevatorArrived, color }) => {
  const distance = Math.abs(y - style.y);
  const duration = distance / ELEVATOR_SPEED * 1000;

  const spring = useSpring({
    from: { y: style.y },
    to: { y },
    config: {
      clamp: true,
      duration,
      mass: 20,
      tension: 80,
      friction: 14,
    },
    onRest: () => {
      handleElevatorArrived();
    },
  });

  const translateY = spring.y.interpolate({
    range: [style.y, y],
    output: [style.y, y].map(y => `translateY(${y}px)`),
  });

  return (
    <animated.div
      className="elevator"
      style={{
        ...style,
        transform: translateY,
      }}
    >
      <img className="elevator-image" src={`./icons8-elevator-${color}.svg`} alt = "Woops"></img>
    </animated.div>
  );
};

export default Elevator;
