import React, { useEffect, useRef, useState } from 'react';
import '../styles/boardStyles.css';
import Elevator from './Elevator';
import useElevatorController from '../hooks/useElevatorController';

const Board = ({ rows, columns }) => {
  const squareRef = useRef(null);
  const [width, setWidth] = useState(0);
  const [height, setHeight] = useState(0);
  const { buttonColors, handleElevatorReservation, handleElevatorArrived, setElevatorsData } =
    useElevatorController(columns, rows, height);

  // Initialize elevator data
  useEffect(() => {
    if (squareRef.current) {
      const { width, height } = squareRef.current.getBoundingClientRect();
      setWidth(width);
      setHeight(height);

      const elevators = Array.from({ length: columns }, (_, i) => ({
        key: i,
        y: 0,
        style: { backgroundColor: 'green', width: `${width}px`, left: `${i * (width - 2)}px` },
        handleElevatorArrived: handleElevatorArrived(i, 0),
        occupied: false,
        currFloor: 0,
        toFloor: -1,
      }));
      setElevatorsData(elevators);
    }
  }, []);

  // Create board rows and columns
  const createBoard = () => {
    return Array.from({ length: rows }, (_, i) => {
      const floor = rows - i - 1;
      const isGroundFloor = floor === 0;
      const rowNumber = isGroundFloor ? 'Ground Floor' : floor === 1 ? '1st' : `${floor}th`;
      const squareElements = Array.from({ length: columns }, (_, j) => (
        <div key={`cell-${i}-${j}`} className="square p-2 bg-white" ref={i === 0 && j === 0 ? squareRef : null} />
      ));
      return (
        <div key={`row-${i}`} className="d-flex flex-row align-items-center">
          <div className="row-number me-2">{rowNumber}</div>
          {squareElements}
          <div className="moving-object-wrapper" style={{ position: 'relative', display: 'inline-block' }}>
            <button
              type="button"
              id={`button-${floor}`}
              disabled={false}
              className="elevator-button-arrived"
              style={{ backgroundColor: buttonColors[floor] }}
              onClick={() => handleElevatorReservation(floor)}
            >
              Call
            </button>
          </div>
        </div>
      );
    });
  };

  return (
    <div className="board">
      {createBoard()}
      {elevatorsData.map(data => (
        <Elevator
          key={data.key}
          y={data.y}
          style={data.style}
          handleElevatorArrived={data.handleElevatorArrived}
        />
      ))}
    </div>
  );
};

export default Board;
